import cv2
import numpy as np
import sys
import time
import os
import concurrent.futures

# Video specifications
width, height = 1920, 1080
fps = 30
video_filename = "pi6.mp4"

# Text specifications
text = ""
font = cv2.FONT_HERSHEY_SIMPLEX
font_scale = 2
thickness = 3
text_color = (255, 255, 255)  # White text

# Determine text size (width and height)
(text_width, text_height), baseline = cv2.getTextSize(text, font, font_scale, thickness)

# Scrolling parameters
speed_pixels_per_sec = 300  # Pixels per second
shift_per_frame = speed_pixels_per_sec / fps

# Total shift calculations
total_shift = width + text_width
duration_sec = total_shift / speed_pixels_per_sec
total_frames = int(duration_sec * fps)

# Precompute constant vertical position
y = int((height + text_height) / 2)

# Pre-render the text image once
def create_text_template():
    # Make it wider than needed to handle edge cases
    template_width = text_width + 100
    template = np.zeros((height, template_width, 3), dtype=np.uint8)
    cv2.putText(template, text, (0, y), font, font_scale, text_color, thickness, cv2.LINE_AA)
    return template

# Function to print progress bar
def print_progress(current, total, bar_length=50):
    fraction = current / total
    arrow = int(fraction * bar_length) * "#"
    padding = (bar_length - len(arrow)) * "-"
    percent = int(fraction * 100)
    sys.stdout.write(f"\rProgress: [{arrow}{padding}] {percent}%")
    sys.stdout.flush()

# Function to generate a frame
def generate_frame(args):
    frame_index, x, text_template = args
    
    # Create empty frame (much faster than np.zeros)
    frame = np.zeros((height, width, 3), dtype=np.uint8)
    
    # Determine the visible part of the text template
    if x < 0:
        # Text has scrolled partially off the left edge
        visible_width = min(text_width + x, width)
        if visible_width <= 0:
            return frame  # Text is completely off-screen
            
        # Copy the visible portion of the text template to the frame
        template_start = -x
        frame[:, 0:visible_width] = text_template[:, template_start:template_start+visible_width]
    else:
        # Text is fully or partially on screen from the right
        visible_width = min(text_width, width - x)
        if visible_width <= 0:
            return frame  # Text is completely off-screen
            
        # Copy the visible portion of the text template to the frame
        frame[:, x:x+visible_width] = text_template[:, 0:visible_width]
    
    return frame

if __name__ == "__main__":
    start_time = time.time()
    
    # Precompute x positions for all frames
    x_positions = [int(width - shift_per_frame * i) for i in range(total_frames)]
    
    # Create text template once
    text_template = create_text_template()
    
    # Setup video writer
    fourcc = cv2.VideoWriter_fourcc(*'mp4v')
    video_writer = cv2.VideoWriter(video_filename, fourcc, fps, (width, height))
    
    # Calculate optimal batch size based on system
    num_workers = min(32, (os.cpu_count() or 1) * 2)
    batch_size = 100  # Process frames in batches
    
    # Process in batches to reduce overhead
    for batch_start in range(0, total_frames, batch_size):
        batch_end = min(batch_start + batch_size, total_frames)
        current_batch = range(batch_start, batch_end)
        
        # Create arguments for each frame in the batch
        args = [(i, x_positions[i], text_template) for i in current_batch]
        
        # Use ThreadPoolExecutor for parallel frame generation
        with concurrent.futures.ThreadPoolExecutor(max_workers=num_workers) as executor:
            # Process frames in parallel
            frames = list(executor.map(generate_frame, args))
            
            # Write frames to video
            for frame in frames:
                video_writer.write(frame)
                
        # Update progress
        print_progress(batch_end, total_frames)
    
    # Release the video writer
    video_writer.release()
    
    # Print final statistics
    end_time = time.time()
    total_time = end_time - start_time
    print(f"\nVideo saved as {video_filename}")
    print(f"Total execution time: {total_time:.2f} seconds")